import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [description, setDescription] = useState('');
  const [storyData, setStoryData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingProgress, setLoadingProgress] = useState('');
  const [error, setError] = useState(null);
  const [isAudioPlaying, setIsAudioPlaying] = useState(false); // New state for audio playing

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setPreviewUrl(URL.createObjectURL(file));
      setDescription('');
      setStoryData(null);
      setError(null);
    }
  };

  const generateDescription = async () => {
    // ... (existing code)
  };

  const generateVisualStory = async () => {
    // ... (existing code)
  };

  const hearStory = async () => {
    if (!storyData) {
      setError('No story available to hear.');
      return;
    }

    setLoading(true);
    setLoadingProgress('Playing story...');

    try {
      const response = await axios.post('http://localhost:8080/hear_story', {
        story_text: description // Send the story text to the backend
      });

      if (response.data.success) {
        setIsAudioPlaying(true);
      } else {
        throw new Error('Failed to play story');
      }
    } catch (err) {
      console.error('Audio playback error:', err);
      setError('Error playing story: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
      setLoadingProgress('');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ImageTales</h1>
        <div className="tagline">Uncover the Magic Hidden in Every Moment</div>
      </header>

      <main className="App-main">
        <section className="control-panel">
          <div className="upload-section">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              id="image-upload"
              className="file-input"
            />
            <label htmlFor="image-upload" className="upload-button">
              {selectedImage ? 'Change Image' : 'Upload Image'}
            </label>

            {previewUrl && (
              <div className="preview-container">
                <img src={previewUrl} alt="Preview" className="preview-image" />
              </div>
            )}
          </div>

          <div className="button-container">
            <button
              onClick={generateDescription}
              disabled={!selectedImage || loading}
              className="action-button"
            >
              {loading && !description ? 'Generating...' : 'Generate Scenario'}
            </button>
            <button
              onClick={generateVisualStory}
              disabled={!description || loading}
              className="action-button"
            >
              {loading && description ? 'Generating...' : 'Generate Story'}
            </button>

            {/* Audio Icon Button */}
            <button
              onClick={hearStory}
              disabled={!storyData || loading} // Enable only if storyData is available
              className="action-button"
            >
              🎧 {/* You can replace this with an icon */}
            </button>
          </div>

          {error && <div className="error-message">{error}</div>}
          {loading && (
            <div className="loading">
              <div className="loading-spinner"></div>
              <p>{loadingProgress}</p>
            </div>
          )}
        </section>

        {description && (
          <section className="description-section">
            <h2>STORY SCENARIO</h2>
            <div className="content-box">
              <p>{description}</p>
            </div>
          </section>
        )}

        {storyData && (
          <section className="story-section">
            <h2>GENERATED VISUAL STORY</h2>
            <div className="story-container">
              {storyData.map((segment, index) => (
                <div key={index} className={`story-segment ${index % 2 === 1 ? 'reverse' : ''}`}>
                  <div className="story-image">
                    {segment.data && (
                      <img 
                        src={`data:image/jpeg;base64,${segment.data}`}
                        alt={`Story scene ${index + 1}`}
                        onError={(e) => {
                          console.error('Image loading error for segment:', index, e);
                          e.target.src = 'placeholder.png';
                        }}
                      />
                    )}
                  </div>
                  <div className="story-text">
                    <div className="paragraph">
                      <p>{segment.paragraph}</p>
                    </div>
                    <div className="image-description">
                      <h4>Scene Description:</h4>
                      <p>{segment.description}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}
      </main>
    </div>
  );
}

export default App;
