import React, { useState } from 'react';
import axios from 'axios';

const StoryComponent = () => {
    const [storyText, setStoryText] = useState('');
    const [audioSrc, setAudioSrc] = useState('');

    const handleGenerateStory = async () => {
        try {
            const response = await axios.post('http://localhost:8080/hear_story', {
                // Include any necessary data here
            });

            if (response.data.success) {
                setStoryText(response.data.story_text);
                setAudioSrc(`data:audio/wav;base64,${response.data.audio}`);
            } else {
                console.error(response.data.error);
            }
        } catch (error) {
            console.error('Error generating story:', error);
        }
    };

    return (
        <div>
            <h2>Generated Story</h2>
            <div className="image-description">
                <h4>Story Description</h4>
                <p>{storyText}</p>
            </div>

            <h3>Audio Version</h3>
            {audioSrc && (
                <audio controls>
                    <source src={audioSrc} type="audio/wav" />
                    Your browser does not support the audio element.
                </audio>
            )}

            <button onClick={handleGenerateStory}>Generate Story</button>
        </div>
    );
};

export default StoryComponent;
