import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [story, setStory] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const generateStory = async () => {
    if (!prompt.trim()) {
      setError('Please enter a prompt');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:8080/generate', {
        prompt: prompt
      });

      setStory(response.data);
    } catch (err) {
      setError('Failed to generate story. Please try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Story & Image Generator</h1>
      </header>

      <main className="App-main">
        <div className="input-section">
          <textarea
            className="prompt-input"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your story prompt here..."
            rows={4}
          />
          <button 
            className="generate-button"
            onClick={generateStory}
            disabled={loading}
          >
            {loading ? 'Generating...' : 'Generate Story'}
          </button>
          {error && <div className="error-message">{error}</div>}
        </div>

        {loading && (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Creating your story and generating images...</p>
          </div>
        )}

        {story && (
          <div className="story-container">
            {story.map((segment, index) => (
              <div key={index} className="story-segment">
                <div className="story-image">
                  <img 
                    src={`data:image/jpeg;base64,${segment.data}`}
                    alt={segment.description}
                  />
                  <div className="image-description">
                    <h3>Image Description:</h3>
                    <p>{segment.description}</p>
                  </div>
                </div>
                <div className="story-text">
                  <h3>Story:</h3>
                  <p>{segment.paragraph}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
