import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [description, setDescription] = useState('');
  const [storyData, setStoryData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentStep, setCurrentStep] = useState('upload'); // upload, description, story

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setPreviewUrl(URL.createObjectURL(file));
      setDescription('');
      setStoryData(null);
      setError(null);
      setCurrentStep('upload');
    }
  };

  const generateDescription = async () => {
    if (!selectedImage) {
      setError('Please select an image first');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      const response = await axios.post('http://localhost:8080/generate_description', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      if (response.data.success) {
        setDescription(response.data.description);
        setCurrentStep('description');
      } else {
        throw new Error(response.data.error || 'Failed to generate description');
      }
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  const generateVisualStory = async () => {
    if (!description) {
      setError('Please generate a description first');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:8080/generate_visual_story');

      if (response.data.success) {
        setStoryData(response.data.images);
        setCurrentStep('story');
      } else {
        throw new Error(response.data.error || 'Failed to generate story');
      }
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Visual Story Generator</h1>
        <p>Transform your images into captivating stories with AI-generated visuals</p>
      </header>

      <main className="App-main">
        <div className="progress-bar">
          <div className={`progress-step ${currentStep === 'upload' ? 'active' : ''}`}>
            1. Upload Image
          </div>
          <div className={`progress-step ${currentStep === 'description' ? 'active' : ''}`}>
            2. Generate Description
          </div>
          <div className={`progress-step ${currentStep === 'story' ? 'active' : ''}`}>
            3. Create Story
          </div>
        </div>

        <section className="upload-section">
          <div className="upload-container">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="file-input"
              id="image-upload"
            />
            <label htmlFor="image-upload" className="upload-label">
              {previewUrl ? 'Change Image' : 'Choose an Image'}
            </label>
          </div>

          {previewUrl && (
            <div className="preview-container">
              <img src={previewUrl} alt="Preview" className="preview-image" />
            </div>
          )}

          <div className="button-group">
            <button
              onClick={generateDescription}
              disabled={!selectedImage || loading}
              className="action-button"
            >
              {loading ? 'Generating Description...' : 'Generate Description'}
            </button>
            {description && (
              <button
                onClick={generateVisualStory}
                disabled={loading}
                className="action-button story-button"
              >
                {loading ? 'Creating Story...' : 'Generate Visual Story'}
              </button>
            )}
          </div>

          {error && <div className="error-message">{error}</div>}
        </section>

        {loading && (
          <div className="loading-overlay">
            <div className="loading-content">
              <div className="loading-spinner"></div>
              <p>{currentStep === 'description' ? 'Analyzing image...' : 'Creating your story...'}</p>
            </div>
          </div>
        )}

        {description && (
          <section className="result-section description-section">
            <h2>Image Description</h2>
            <div className="description-box">
              <p>{description}</p>
            </div>
          </section>
        )}

        {storyData && (
          <section className="result-section story-section">
            <h2>Your Visual Story</h2>
            <div className="story-container">
              {storyData.map((segment, index) => (
                <div key={index} className="story-segment">
                  <div className="story-image">
                    <img
                      src={`data:image/jpeg;base64,${segment.data}`}
                      alt={`Story segment ${index + 1}`}
                    />
                  </div>
                  <div className="story-content">
                    <p className="story-text">{segment.paragraph}</p>
                    <p className="image-description">
                      <strong>Image Prompt:</strong> {segment.description}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}
      </main>
    </div>
  );
}

export default App;
