import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [description, setDescription] = useState('');
  const [visualStory, setVisualStory] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setPreviewUrl(URL.createObjectURL(file));
      // Reset previous results
      setDescription('');
      setVisualStory(null);
      setError(null);
    }
  };

  const generateDescription = async () => {
    if (!selectedImage) {
      setError('Please select an image first');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      const response = await axios.post('http://localhost:8080/generate_description', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setDescription(response.data.description);
    } catch (err) {
      setError('Failed to generate description: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const generateVisualStory = async () => {
    if (!description) {
      setError('Please generate a description first');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:8080/generate_visual_story');
      setVisualStory(response.data);
    } catch (err) {
      setError('Failed to generate visual story: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Visual Story Generator</h1>
        <p>Upload an image to generate a story with matching visuals</p>
      </header>

      <main className="App-main">
        <section className="upload-section">
          <div className="upload-container">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="file-input"
              id="image-upload"
            />
            <label htmlFor="image-upload" className="upload-label">
              {previewUrl ? 'Change Image' : 'Choose an Image'}
            </label>
          </div>

          {previewUrl && (
            <div className="preview-container">
              <img src={previewUrl} alt="Preview" className="preview-image" />
            </div>
          )}

          <div className="button-group">
            <button
              onClick={generateDescription}
              disabled={!selectedImage || loading}
              className="action-button"
            >
              {loading ? 'Generating...' : 'Generate Description'}
            </button>
            <button
              onClick={generateVisualStory}
              disabled={!description || loading}
              className="action-button"
            >
              {loading ? 'Generating...' : 'Generate Visual Story'}
            </button>
          </div>

          {error && <div className="error-message">{error}</div>}
        </section>

        {loading && (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Please wait while we process your request...</p>
          </div>
        )}

        {description && (
          <section className="result-section">
            <h2>Image Description</h2>
            <div className="description-box">
              <p>{description}</p>
            </div>
          </section>
        )}

        {visualStory && (
          <section className="result-section">
            <h2>Visual Story</h2>
            <div className="story-container">
              {visualStory.map((segment, index) => (
                <div key={index} className="story-segment">
                  <div className="story-image">
                    <img
                      src={`data:image/jpeg;base64,${segment.data}`}
                      alt={`Story segment ${index + 1}`}
                    />
                  </div>
                  <div className="story-content">
                    <div className="story-description">
                      <h3>Scene Description:</h3>
                      <p>{segment.description}</p>
                    </div>
                    <div className="story-paragraph">
                      <h3>Story:</h3>
                      <p>{segment.paragraph}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}
      </main>
    </div>
  );
}

export default App;
