import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [description, setDescription] = useState('');
  const [visualStory, setVisualStory] = useState(null);
  const [audioStory, setAudioStory] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setSelectedImage(file);
  };

  const generateDescription = async () => {
    if (!selectedImage) {
      alert('Please select an image first');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      const response = await axios.post('http://localhost:8080/generate_description', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setDescription(response.data.description);
    } catch (error) {
      console.error('Error generating description:', error);
      alert('Error generating description');
    } finally {
      setLoading(false);
    }
  };

  const generateVisualStory = async () => {
    if (!selectedImage) {
      alert('Please select an image first');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      const response = await axios.post('http://localhost:8080/generate_visual_story', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.error) {
        throw new Error(response.data.error);
      }

      setVisualStory(response.data);
    } catch (error) {
      console.error('Error generating visual story:', error);
      alert(error.response?.data?.error || error.message || 'Error generating visual story');
    } finally {
      setLoading(false);
    }
};

  const generateAudioStory = async () => {
    if (!selectedImage) {
      alert('Please select an image first');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      const response = await axios.post('http://localhost:8080/Hear_story', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setAudioStory(response.data);
      setVisualStory(response.data.story);
    } catch (error) {
      console.error('Error generating audio story:', error);
      alert('Error generating audio story');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Story Voice Generator</h1>
      </header>

      <main className="App-main">
        <div className="upload-section">
          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="file-input"
          />
          <div className="button-group">
            <button 
              onClick={generateDescription}
              disabled={!selectedImage || loading}
              className="action-button"
            >
              Generate Description
            </button>
            <button 
              onClick={generateVisualStory}
              disabled={!selectedImage || loading}
              className="action-button"
            >
              Generate Visual Story
            </button>
            <button 
              onClick={generateAudioStory}
              disabled={!selectedImage || loading}
              className="action-button"
            >
              Generate Audio Story
            </button>
          </div>
        </div>

        {loading && (
          <div className="loading">Processing your request...</div>
        )}

        {description && (
          <div className="description-section">
            <h2>Image Description</h2>
            <div className="content-box">
              {description}
            </div>
          </div>
        )}

        {visualStory && (
          <div className="story-container">
            {visualStory.map((item, index) => (
              <div key={index} className="story-segment">
                <img 
                  src={`data:image/jpeg;base64,${item.data}`} 
                  alt={`Generated ${index + 1}`}
                  className="generated-image"
                />
                <div className="text-content">
                  <p className="description"><strong>Description:</strong> {item.description}</p>
                  <p className="paragraph">{item.paragraph}</p>
                </div>
              </div>
            ))}
          </div>
        )}

        {audioStory && audioStory.audio && (
          <div className="audio-section">
            <h2>Listen to the Story</h2>
            <audio 
              controls 
              src={`data:audio/mp3;base64,${audioStory.audio}`}
              className="audio-player"
            >
              Your browser does not support the audio element.
            </audio>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
